mapper.select:
select u.*,o.id as orgId,o.orgName,o.orgCode,o.orgLevel,o.description from sys_user u,sys_org o where u.removed=0 AND o.id=u.org_id

    and u.id = #{id}


    and u.loginName = #{loginName}


    and u.username like concat('%',#{username},'%')


    and u.enabled = #{enabled} 


    and u.mpNum = #{mpNum}


    and u.groupid = #{groupid}


    and u.userType = #{userType}


    and u.uniqueCode = #{uniqueCode}


    and u.openid = #{openid}


    and u.accesstoken = #{accesstoken}


    and u.userCode = #{userCode}


    and u.phone like concat('%',#{phone},'%')


    and u.email = #{email}


    and u.qqCode = #{qqCode}


    and u.weChatCode = #{weChatCode}


    and u.weiboCode = #{weiboCode}


    and u.alipayCode = #{alipayCode}


    and u.position = #{position}


    and u.ownerOrgId = #{ownerOrgId}


    and u.officePhone  like concat('%',#{officePhone},'%')


    and u.org_id = #{sysOrg.id}


    and o.parent_id = #{sysOrg.parent.id}


    ORDER BY ${orderByClause}


    ORDER BY ISNULL(u.userLevel) ASC, u.userLevel ASC, ISNULL(u.orderBy) ASC, u.orderBy ASC, loginName ASC

select u.*,o.id as orgId,o.orgName,o.orgCode,o.orgLevel,o.description from sys_user u,sys_org o where u.removed=0 AND o.id=u.org_id

    and u.id = #{id}


    and u.loginName = #{loginName}


    and u.username like concat(concat('%',#{username}),'%')


    and u.enabled = #{enabled} 


    and u.mpNum = #{mpNum}


    and u.groupid = #{groupid}


    and u.userType = #{userType}


    and u.uniqueCode = #{uniqueCode}


    and u.openid = #{openid}


    and u.accesstoken = #{accesstoken}


    and u.userCode = #{userCode}


    and u.phone like concat(concat('%',#{phone}),'%')


    and u.email = #{email}


    and u.qqCode = #{qqCode}


    and u.weChatCode = #{weChatCode}


    and u.weiboCode = #{weiboCode}


    and u.alipayCode = #{alipayCode}


    and u.position = #{position}


    and u.ownerOrgId = #{ownerOrgId}


    and u.officePhone like concat(concat('%',#{officePhone}),'%')


    and u.org_id = #{sysOrg.id}


    and o.parent_id = #{sysOrg.parent.id}


    ORDER BY ${orderByClause}


    ORDER BY NVL(u.userLevel,0) ASC, u.userLevel ASC, NVL(u.orderBy,0) ASC, u.orderBy ASC, loginName ASC

select u.*,o.id as orgId,o.orgName,o.orgCode,o.orgLevel from sys_user u,sys_org o,sys_group g,sys_user_group ug where 
u.removed=0 AND o.id=u.org_id and u.id = ug.user_id and ug.group_id = g.id and g.id = #{groupid}

    and u.enabled = #{enabled} 


    and u.userType = #{userType}


    and u.position = #{position}


    and u.ownerOrgId = #{ownerOrgId}


    and u.org_id = #{sysOrg.id}


    and o.parent_id = #{sysOrg.parent.id}


    ORDER BY ${orderByClause}


    ORDER BY ISNULL(u.userLevel) ASC, u.userLevel ASC, ISNULL(u.orderBy) ASC, u.orderBy ASC, loginName ASC

