mapper.update:
update sys_user
loginName=#{loginName}, userCode=#{userCode}, uniqueCode=#{uniqueCode}, username=#{username}, phone=#{phone}, email=#{email}, position=#{position}, officePhone=#{officePhone}, qqCode=#{qqCode}, weChatCode=#{weChatCode}, weiboCode=#{weiboCode}, alipayCode=#{alipayCode}, sceneCode=#{sceneCode}, sceneValue=#{sceneValue}, tag=#{tag}, userType=#{userType}, userLevel=#{userLevel}, orderBy=#{orderBy}, ownerOrgId=#{ownerOrgId}, hierarchyOrgIds=#{hierarchyOrgIds}, mpNum=#{mpNum}, mpName=#{mpName}, openid=#{openid}, unionid=#{unionid}, subscribe=#{subscribe}, nickname=#{nickname}, sex=#{sex}, country=#{country}, province=#{province}, city=#{city}, language=#{language}, headimgurl=#{headimgurl}, subscribe_time=#{subscribe_time}, groupid=#{groupid}, backgroundurl=#{backgroundurl}, signature=#{signature}, birthDate=#{birthDate}, enabled=#{enabled}, removed=#{removed}, org_id=#{sysOrg.id}, updateUserId=#{updateUserId}, updateUserCode=#{updateUserCode}, updateUserName=#{updateUserName}, updateDate=#{updateDate}
where id=#{id}
